import React, { useCallback } from 'react';
import styles from './filterBox.module.css';
import { useState } from 'react';
import axios from 'axios';

const FilterBox = () => {
    const [changeColor, setChangeColor] = useState(true);
    const [filterState, setFilterState] = useState({
        passingTags: {
            Lager: false,
            Ale: false,
            IPA: false,
            Stout: false
        }
    });

    const handleFilter = (e) => {
        const beerType = e.target.id;
        setFilterState({
            passingTags: {...filterState.passingTags, [beerType]: !filterState.passingTags[beerType]}  // 클릭하면 true, false로 바꿔주기
        });
        // console.log(filterState.passingTags[beerType]);
        if (filterState.passingTags[beerType] === false) {
            let cur = document.getElementById(beerType);
            cur.style.color = "white";
            cur.style.backgroundColor = "#FDC74B";
        } 
        
        if (filterState.passingTags[beerType] === true) {
            let cur = document.getElementById(beerType);
            cur.style.color = "#FDC74B";
            cur.style.backgroundColor = "white";
        }

        setChangeColor(changeColor);
    };

    const filteredCollected = useCallback(() => {
        const clicked = [];
        const now = filterState.passingTags;

        for (let item in now) {
            if(now[item]) {
                // clicked.now = item; -> 현재 클릭된 아이템
                clicked.push(item);
            }
        }

        return clicked;
    }, [filterState.passingTags]);

    const result = filteredCollected();
    console.log(result);
    const getBeerList = async (e) => {
        const request_data = {'beerTypeList': result}; // type of -> Object
        let response = await axios({
                method: 'post',
                url: '/api/filter',
                headers: {'Content-Type': 'application/json'},
                data: JSON.stringify(request_data)
            });
        console.log('response: ', response);
    };
    getBeerList();

    return (
        <div className={styles.container}>
            <button id="Lager" className={styles.box} onClick={handleFilter}>라거</button>
            <button id="Ale" className={styles.box} onClick={handleFilter}>에일</button>
            <button id="IPA" className={styles.box} onClick={handleFilter}>IPA</button>
            <button id="Stout" className={styles.box} onClick={handleFilter}>스타우트</button>
        </div>
    );
};

export default FilterBox;
